import React from 'react'

function card(props) {

	const dragStart = e=> {
		const target = e.target;

		e.dataTransfer.setData('card_id',target.id);
		e.setTrigger(false);

		// setTimeout(() => {
		// 	target.style.display = "none";
		// 	}, 0);
	}

	const dragOver = e=> {
		e.stopPropagation();
	}


	return (
		<div
		id = {props.id}
		className={props.className}
		draggable={props.draggable}
		onDragStart={dragStart}
		onDragOver={dragOver}
		>

			{props.children}

		</div>
	)
}

export default card
