import Popup from './componets/Popup';
import {useState} from'react';


function App() {
  const [buttonPopup, setButtonPopup] = useState(false);

  return (
    <div className="App">
      <main>
        <h1>Recat Popups</h1>
        <br></br>
        <button onClick={() => setButtonPopup(true)}>Widgets</button>
      </main>
        <Popup trigger={buttonPopup}>
          <h3>My popup</h3>
          <p>this is my button trigger</p>
        </Popup>
    </div>
  );
}

export default App;
