import React from 'react'

function Boards(props) {

	const drop = e =>
	{
		e.preventDefault();
		const card_id = e.dataTransfer.getData('card_id'); //무슨함수??

		const card = document.getElementById(card_id); //무슨함수?
		card.style.display = 'block';

		e.target.appendChild(card); //무슨함수??
	}

	const dragOver = e =>
	{
		e.preventDefault();//무슨뜼일까
	}
	return (
		<div
			id={props.id}
			className={props.className}
			onDrop={drop}
			onDragOver={dragOver}
			>
			{ props.children}
		</div>
	)
}

export default Boards
